
export class EventUpdater {
    lastYear: number;
    events: Events;
    lastEvent: string;
    lastMonth: number;
    constructor() {
        this.events = new Events();
    }
    timeForChange(d: Date): boolean {
        let year = d.getFullYear();
        if (year == 2020) {
            if (d.getMonth() == this.lastMonth)
                return false;
            else {
                this.lastMonth = d.getMonth();
                return true;
            }
        }
        if (year == this.lastYear) return false;
        return true;
    }
    update(d: Date): string {
        let year = d.getFullYear();
        if (this.timeForChange(d)) {
            let event = this.events.getRandomEvent(year);
            this.lastYear = year;
            console.log(event);
            this.lastEvent = event;
        }
        return this.lastEvent;
    }
}
